import React, {useState, useEffect} from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import LockOpenIcon from '@material-ui/icons/LockOpen';
import LockIcon from '@material-ui/icons/Lock';
import ListItemText from '@material-ui/core/ListItemText';

function Questions({data}){
    // const [isDrawerOpen, setDrawerState] = useState(false);

    // const toggleDrawer = (open) => event => {
    //     if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
    //         return;
    //     }

    //     setDrawerState(open);
    // };

    return (
        <List>
            <Items data={data}></Items>
        </List>
    )
}

function Items({data}){
    return data.map(item => {
        return (
            <div>
                <Question question={item.question} type={'english'}></Question>
                <Answer question={item.answer} type={'english'}></Answer>

                // <Question answer={item.question} type={'romaji'}></Question>
                // <Answer answer={item.answer} type={'romaji'}></Answer>
            </div>
        )
    });
}

function Question({question, type}){
    console.log(question)
    return (
        <ListItem button>
            <ListItemIcon>
            <LockOpenIcon />
            </ListItemIcon>
            <ListItemText primary={`${question[type]}`} />
        </ListItem>
    )
}

function Answer({answer, type}){
    return (
        <ListItem button>
            <ListItemIcon>
            <LockOpenIcon />
            </ListItemIcon>
            <ListItemText primary={`${answer[type]}`} />
        </ListItem>
    )
}


export default Questions;
