import React, {useState, useEffect} from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import LockOpenIcon from '@material-ui/icons/LockOpen';
import LockIcon from '@material-ui/icons/Lock';
import ListItemText from '@material-ui/core/ListItemText';
import Divider from '@material-ui/core/Divider';
import * as _ from 'lodash';

function Questions({data}){
    const [selectedItem, setSelectedItem] = useState({index: -1, type: ''});

    function handleClick(index, type){
        setSelectedItem({index, type});
    }

    return (
        <List>
            <Items data={data} handleClick={handleClick} selectedItem={selectedItem}></Items>
        </List>
    )
}

function Items({data, handleClick, selectedItem}){
    return data.map((item, i) => {
        return (
            <div key={i}>
                {
                    _.map(item.question, (question, key) => {
                        console.log(question)
                        let isSelected = (selectedItem.index === i && selectedItem.type === key) || key === 'romaji';
                        return (
                            <Question question={question[key]} type={key} clickFn={handleClick} isSelected={isSelected}></Question>
                        )
                    })
                }
                {
                    _.map(item.answer, (answer, key) => {
                        let isSelected = (selectedItem.index === i && selectedItem.type === key) || key === 'romaji';
                        return (
                            <Answer answer={answer[key]} type={key} clickFn={handleClick} isSelected={isSelected}></Answer>
                        )
                    })
                }
                <Divider light />
            </div>
        )
    });
}

function Question({question, type, clickFn, isSelected}){
    console.log(type)
    const text = isSelected ? question : type;
    return (
        <ListItem button onClick={clickFn}>
            <ListItemIcon>
            <LockOpenIcon />
            </ListItemIcon>
            <ListItemText primary={`${text}`} />
        </ListItem>
    )
}

function Answer({answer, type, clickFn, isSelected}){
    const text = isSelected ? answer : type;
    return (
        <ListItem button onClick={clickFn}>
            <ListItemIcon>
            <LockOpenIcon />
            </ListItemIcon>
            <ListItemText primary={`${text}`} />
        </ListItem>
    )
}


export default Questions;
