import React, {useState, useEffect} from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import LockOpenIcon from '@material-ui/icons/LockOpen';
import LockIcon from '@material-ui/icons/Lock';
import GraphicEqIcon from '@material-ui/icons/GraphicEq';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import IconButton from '@material-ui/core/IconButton';
import Divider from '@material-ui/core/Divider';
import Player from './Player';
import * as _ from 'lodash';

const defaultKeyShow = 'katakana';

function Questions({data}){
    const filtered = _.filter({...data}, o=>{
        return o.katakana !== null;
    })
    const [selectedItem, setSelectedItem] = useState({index: -1, type: ''});

    function handleClick(val){
        setSelectedItem(val);
    }

    return (
        <List>
            <Items data={filtered} handleClick={handleClick} selectedItem={selectedItem}></Items>
        </List>
    )
}

function Items({data, handleClick, selectedItem}){
    return data.map((item, i) => {
        return (
            <div key={i} className="question-container" style={{display: 'flex', justifyContent: 'space-between'}}>
                <div className="question">
                    <ListItem button>
                        <ListItemText primary={`Q: ${item.question[defaultKeyShow]}`}/>
                        {
                            item.question.audio ?
                                <Player url={item.question.audio}></Player>
                            :
                                null
                        }
                    </ListItem>
                    {
                        _.map(item.question, (question, key) => {
                            if(key !== defaultKeyShow && key !== 'audio'){
                                let isSelected = selectedItem.index === i && selectedItem.type === key;
                                let item = {index: i, type: key};
                                return (
                                    <Question question={question} type={key} clickFn={handleClick} isSelected={isSelected} item={item}></Question>
                                )
                            }
                        })
                    }
                </div>
                <div className="answer">
                    <ListItem>
                        <ListItemText primary={`A: ${item.answer[defaultKeyShow]}`}/>
                        {
                            item.answer.audio ?
                                <ListItemSecondaryAction>
                                    <IconButton edge="start" aria-label="listen">
                                        <GraphicEqIcon/>
                                    </IconButton>
                                </ListItemSecondaryAction>
                            :
                            null
                        }
                    </ListItem>
                    {
                        _.map(item.answer, (answer, key) => {
                            if(key !== defaultKeyShow && key !== 'audio'){
                                let isSelected = selectedItem.index === i && selectedItem.type === key;
                                let item = {index: i, type: key};
                                return (
                                    <Answer answer={answer} type={key} clickFn={handleClick} isSelected={isSelected} item={item}></Answer>
                                )
                            }
                        })
                    }
                </div>
                <Divider light />
            </div>
        )
    });
}

function Question({question, type, clickFn, isSelected, item}){
    const text = isSelected ? question : type;
    return (
        <ListItem button onClick={()=>{clickFn(item)}}>
            <ListItemIcon>
            {   
                isSelected ?
                    <LockOpenIcon />
                :
                    <LockIcon />
            }
            </ListItemIcon>
            <ListItemText primary={`${text}`} />
        </ListItem>
    )
}

function Answer({answer, type, clickFn, isSelected, item}){
    const text = isSelected ? answer : type;
    return (
        <ListItem button onClick={()=>{clickFn(item)}}>
            <ListItemIcon>
            {   
                isSelected ?
                    <LockOpenIcon />
                :
                    <LockIcon />
            }
            </ListItemIcon>
            <ListItemText primary={`${text}`} />            
        </ListItem>
    )
}


export default Questions;
