import React, {useState, useEffect} from 'react';
import List from '@material-ui/core/List';
import Button from '@material-ui/core/Button';
import Player from './Player';
import * as _ from 'lodash';


function Card({card, isMobile}){
    const [isShowAnswer, setIsShowAnswer] = useState(false);
    const [isShowHint, setIsShowHint] = useState(false);

    console.log(isShowAnswer)

    return (
        <div>
            <div className="study-text">{card.hiragana}</div>

            {
                isShowHint ? 
                    <div className="hint-text">{card.romaji}</div>
                    :
                    <Button color="secondary" onClick={()=>setIsShowHint(true)}>Show Hint</Button>
            }

            {
                card.audioUrl ?
                    <Player url={card.audioUrl} controls={isMobile}></Player>
                :
                    null
            }

            {
                isShowAnswer ? 
                    <div className="answer-text">{card.english}</div>
                    :
                    <Button color="primary" onClick={()=>setIsShowAnswer(true)}>Show Answer</Button>
            }
            <div></div>
        </div>
    )
}

function FlashCards({data}){
    const [currentCardIndex, setCurrentCardIndex] = useState(0);

    function setNext(isRepeat){
        if(!isRepeat){
            //dispatch with no repeat flag
        }
        setCurrentCardIndex(currentCardIndex++);
    }


    const currentCard = data[currentCardIndex];
    return currentCard ? (
        <div>
            <List>
                <Card card={currentCard}></Card>
            </List>

            <div style={{display: 'flex'}}>
                <Button variant="contained" color="secondary" onClick={()=>setNext(true)}>
                    Repeat
                </Button>
                <Button variant="contained" color="primary" onClick={()=>setNext(false)}>
                    Good
                </Button>
            </div>

            <div>{currentCardIndex+1} / {data.length}</div>
        </div>
    ) : null
}

export default FlashCards;
