import React, {useState, useEffect} from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import LockOpenIcon from '@material-ui/icons/LockOpen';
import LockIcon from '@material-ui/icons/Lock';
import QuestionAnswerIcon from '@material-ui/icons/QuestionAnswer';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import IconButton from '@material-ui/core/IconButton';
import Divider from '@material-ui/core/Divider';
import Player from './Player';
import * as _ from 'lodash';

const defaultKeyShow = 'katakana';

function Phrases({data}){
    const [selectedItem, setSelectedItem] = useState({index: -1, type: ''});

    function handleClick(val){
        setSelectedItem(val);
    }

    return (
        <List>
            <Items data={data} handleClick={handleClick} selectedItem={selectedItem}></Items>
        </List>
    )
}

function Items({data, handleClick, selectedItem}){
    return data.map((item, i) => {
        return (
            <div key={i} className="phrase-container" style={{display: 'flex', justifyContent: 'space-between'}}>
                <div className="phrase">
                    <ListItem button className={'button'}>
                        <ListItemIcon>
                            <QuestionAnswerIcon/>
                        </ListItemIcon>
                        <ListItemText primary={`${item[defaultKeyShow]}`}/>
                        {
                            item.audio ?
                                <ListItemSecondaryAction>
                                    <IconButton edge="start" aria-label="listen">
                                        <Player url={item.audio}></Player>
                                    </IconButton>
                                </ListItemSecondaryAction>
                            :
                                null
                        }
                    </ListItem>
                    {
                        _.map(item, (phrase, key) => {
                            if(key !== defaultKeyShow){
                                let isSelected = selectedItem.index === i && selectedItem.type === key;
                                let item = {index: i, type: key};
                                return (
                                    <Phrase phrase={phrase} type={key} clickFn={handleClick} isSelected={isSelected} item={item}></Phrase>
                                )
                            }
                        })
                    }
                    <Divider light />
                </div>
            </div>
        )
    });
}

function Phrase({phrase, type, clickFn, isSelected, item}){
    const text = isSelected ? phrase : type;
    return (
        <ListItem button onClick={()=>{clickFn(isSelected ? {} : item)}}>
            <ListItemIcon>
                <TranslateIcon />
            </ListItemIcon>
            <ListItemText primary={`${text}`} />
            {   
                isSelected ?
                    <VisibilityIcon/>
                :
                    <VisibilityOffIcon/>
            }
        </ListItem>
    )
}


export default Phrases;
