import React, {useState, useEffect} from 'react';
import './App.css';
import { useDispatch, useSelector } from "react-redux";
import * as cardActions from './actions/cardActions';
import {BrowserRouter, Switch, Route, Redirect, withRouter } from "react-router-dom";
import Directory from './components/Directory';
import * as _ from 'lodash';

function App() {
  const cards = useSelector(state => state.cards);
  const dispatch = useDispatch();
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    setIsMobile(window.outerWidth <= 767);

    const handleResize = () => {
      setIsMobile(window.outerWidth <= 767);
    };

    window.addEventListener("resize", handleResize, { passive: true });

    return () => {
      window.removeEventListener("resize", handleResize, true);
    }
  },[]);

  useEffect(()=>{
    dispatch(cardActions.getCards())
  },[]);

  const data = cards || null;
  const questions = _.filter(data, o=>{
    return o.type === "question";
  });
  const answers = _.filter(data, o=>{
    return o.type === "answer";
  });
  const phrases = _.filter(data, o=>{
    return o.type === "phrase";
  });

  return data ? (
    <div className="App" style={{width: '85%', margin: '0 auto'}}>      
      <BrowserRouter>
          <main className="main-content">
              <Switch>
                  <Route exact path="/" render={(props) => <Directory isMobile={isMobile}/>} />
                  <Redirect to="/"/>
              </Switch>
          </main>
      </BrowserRouter>
    </div>
  ) : null;
}

export default App;
