import React, {useState} from 'react';
import useForm from "./useForm";
import validate from './useValidatiom';

function AddCard() {
    const { errors, values, handleChange, handleSubmit, handleClearValues } = useForm(submit, validate);

    const [isFormSubmit, setFormSubmit] = useState(false);
    const [isLoading, setIsLoading] = useState(false);

    function submit(){
        setIsLoading(true);
        // let url = `${baseURL}${queryString.stringify(values)}`;

        // axios.post(url, {}).then(res => {
        //     handleSubmitReset();
        // }).catch(err => {
        //     handleSubmitReset();
        // });
    }

    function handleSubmitReset(){
        setIsLoading(false);
        setFormSubmit(true);
        handleClearValues();
    }

    return (
        <form id="emailContactForm" onSubmit={handleSubmit}>
                <Spin spin={isLoading} text={'submitting...'}></Spin>
                <div className="grid-container left">
                    <div className="grid-row">
                        <div className="grid-item flex-half">
                            <input aria-label="name" className={`bixal-input ${errors.name ? 'error' : ''}`} type="text" placeholder="NAME *" name="name" onChange={handleChange} value={values.name || ''} />
                        </div>
                        <div className="grid-item flex-half">
                            <input aria-label="phone" className="bixal-input" type="text" placeholder="PHONE" name="phone" onChange={handleChange} value={values.phone || ''} />
                        </div>
                    </div>
                    <div className="grid-row">
                        <div className="grid-item flex-half">
                            <input aria-label="email" className={`bixal-input ${errors.email ? 'error' : ''}`} type="text" placeholder="EMAIL *" name="email" onChange={handleChange} value={values.email || ''} />
                        </div>
                        <div className="grid-item flex-half">
                            <input aria-label="company" className="bixal-input" type="text" placeholder="COMPANY" name="company" onChange={handleChange} value={values.company || ''} />
                        </div>
                    </div>
                    <div className="grid-row">
                        <div className="grid-item flex-full">
                            <textarea aria-label="message" style={{minHeight: '75px'}} className={`bixal-input ${errors.message ? 'error' : ''}`} type="text" placeholder="WANT TO FIND OUT MORE OR INTERESTED IN DOING BUSINESS WITH US? TYPE MESSAGE HERE... *" name="message" onChange={handleChange} value={values.message || ''} />
                        </div>
                    </div>
                    <div className="grid-row">
                        <div className="grid-item">
                            <button aria-label="submit" type="submit" htmltype="submit" className="email-submit-button">
                                <div className="bixal-button-content">
                                    <div>SUBMIT</div>
                                    <Icon type="rightArrow" color={"#fff"}></Icon>
                                </div>
                            </button>
                        </div>
                    </div>
                    {
                        <div style={{textAlign: 'left', marginLeft: '15px'}}>
                            <span className="form-field-error">{errorText}</span>
                        </div>
                    }
                    </div>
            </form>
    )
}

export default AddCard;
