import React, {useState} from 'react';
import useForm from "./useForm";
import validate from './useValidation';
import * as _ from 'lodash';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';

function AddCard() {
    const { errors, values, handleChange, handleSubmit, handleClearValues } = useForm(submit, validate);

    const [isFormSubmit, setFormSubmit] = useState(false);
    const [isLoading, setIsLoading] = useState(false);

    function submit(){
        setIsLoading(true);
        // let url = `${baseURL}${queryString.stringify(values)}`;

        // axios.post(url, {}).then(res => {
        //     handleSubmitReset();
        // }).catch(err => {
        //     handleSubmitReset();
        // });
    }

    function handleSubmitReset(){
        setIsLoading(false);
        setFormSubmit(true);
        handleClearValues();
    }

    return (
        <form id="addCardForm" onSubmit={handleSubmit}>
            <div className="grid-item flex-half">
                <input aria-label="hiragana" className={`${errors.hiragana ? 'error' : ''}`} type="text" placeholder="hiragana" name="hiragana" onChange={handleChange} value={values.hiragana || ''} />
                {
                    errors.hiragana ? 
                        <span>Required.</span>
                    :
                        null
                }
            </div>

            <div className="grid-item flex-half">
                <input aria-label="romaji" className="" type="text" placeholder="romaji" name="romaji" onChange={handleChange} value={values.romaji || ''} />
                {
                    errors.romaji ? 
                        <span>Required.</span>
                    :
                        null
                }
            </div>

            <div className="grid-item flex-half">
                <input aria-label="email" className={`${errors.english ? 'error' : ''}`} type="text" placeholder="english" name="english" onChange={handleChange} value={values.english || ''} />
                {
                    errors.english ? 
                        <span>Required.</span>
                    :
                        null
                }
            </div>

            <div className="grid-item flex-half">
            <Select
                labelId="Type"
                id="type-select"
                value={age}
                onChange={handleChange}
            >
                <MenuItem value={10}>Ten</MenuItem>
                <MenuItem value={20}>Twenty</MenuItem>
                <MenuItem value={30}>Thirty</MenuItem>
            </Select>
                {
                    errors.type ? 
                        <span>Required.</span>
                    :
                        null
                }
            </div>

            <button aria-label="submit" type="submit" htmltype="submit" className="email-submit-button">
                <div className="bixal-button-content">
                    <div>SUBMIT</div>
                </div>
            </button>
        </form>
    )
}

export default AddCard;
