import React, {useState, useEffect} from 'react';
import List from '@material-ui/core/List';
import Button from '@material-ui/core/Button';

import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import VisibilityIcon from '@material-ui/icons/Visibility';
import VisibilityOffIcon from '@material-ui/icons/VisibilityOff';
import TranslateIcon from '@material-ui/icons/Translate';
import FeedbackIcon from '@material-ui/icons/Feedback';
import ListItemText from '@material-ui/core/ListItemText';
import Divider from '@material-ui/core/Divider';
import Player from './Player';
import * as _ from 'lodash';


function Card({card}){
    return (
        <div>
            <div className="study-text">{card.hiragana}</div>
            <div className="answer-text">{card.english}</div>
            <div></div>
        </div>
    )
}

function FlashCards({data}){
    const [currentCardIndex, setCurrentCardIndex] = useState(0);

    console.log(data)

    const currentCard = data[currentCardIndex];
    return currentCard ? (
        <div>
            <List>
                <Card card={currentCard}></Card>
            </List>

            <div>

            </div>

            <div>{currentCardIndex+1} / {data.length}</div>
        </div>
    ) : null
}

export default FlashCards;
