import React, {useState, useEffect} from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import HelpOutlineIcon from '@material-ui/icons/HelpOutline';
import QuestionAnswerIcon from '@material-ui/icons/QuestionAnswer';
import VisibilityIcon from '@material-ui/icons/Visibility';
import VisibilityOffIcon from '@material-ui/icons/VisibilityOff';
import TranslateIcon from '@material-ui/icons/Translate';
import Divider from '@material-ui/core/Divider';
import Player from './Player';
import * as _ from 'lodash';

const defaultKeyShow = 'katakana';

function QuestionsAndAnswers({data}){
    const filtered = _.filter({...data}, o=>{
        return o.katakana !== null;
    })
    const [selectedItem, setSelectedItem] = useState({index: -1, type: ''});

    function handleClick(val){
        setSelectedItem(val);
    }

    return (
        <List>
            <Items data={filtered} handleClick={handleClick} selectedItem={selectedItem}></Items>
        </List>
    )
}

function Items({data, handleClick, selectedItem}){
    return data.map((item, i) => {
        return (
            <div key={i} className="question-container" style={{display: 'flex', justifyContent: 'space-between'}}>
                <div className="question">
                    <ListItem button className={'button'}>
                        <ListItemIcon>
                            <HelpOutlineIcon/>
                        </ListItemIcon>
                        <ListItemText primary={`${item.question[defaultKeyShow]}`}/>
                        {
                            item.question.audio ?
                                <Player url={item.question.audio}></Player>
                            :
                                null
                        }
                    </ListItem>
                    {
                        _.map(item.question, (question, key) => {
                            if(key !== defaultKeyShow && key !== 'audio'){
                                let isSelected = selectedItem.index === i && selectedItem.type === key;
                                let item = {index: i, type: key};
                                return (
                                    <Question question={question} type={key} clickFn={handleClick} isSelected={isSelected} item={item}></Question>
                                )
                            }
                        })
                    }
                </div>
                {
                    item.answer ? 
                        <div className="answer">
                            <ListItem button className={'button'}>
                                <ListItemIcon>
                                    <QuestionAnswerIcon/>
                                </ListItemIcon>
                                <ListItemText primary={`${item.answer[defaultKeyShow]}`}/>
                                {
                                    item.answer.audio ?
                                        <Player url={item.answer.audio}></Player>
                                    :
                                    null
                                }
                            </ListItem>
                            {
                                _.map(item.answer, (answer, key) => {
                                    if(key !== defaultKeyShow && key !== 'audio'){
                                        let isSelected = selectedItem.index === i && selectedItem.type === key;
                                        let item = {index: i, type: key};
                                        return (
                                            <Answer answer={answer} type={key} clickFn={handleClick} isSelected={isSelected} item={item}></Answer>
                                        )
                                    }
                                })
                            }
                        </div>
                    :
                        null
                }
                <Divider light />
            </div>
        )
    });
}

function Question({questions, type, clickFn, isSelected, item}){
    const [selectedQuestion, setSelectedQuestion] = useState('');

    _.map(questions, (question, i)=>{
        return (
            <Item key={i} 
                  index={i} 
                  question={question} 
                  selectedItem={selectedQuestion} 
                  clickFn={setSelectedQuestion}>
            </Item>
        )
    })


    return (
        <ListItem button onClick={()=>{clickFn(isSelected ? {} : item)}}>
            <ListItemIcon>
                <TranslateIcon />
            </ListItemIcon>
            <ListItemText primary={`${text}`} />
            {   
                isSelected ?
                    <VisibilityIcon/>
                :
                    <VisibilityOffIcon/>
            }
        </ListItem>
    )
}

function Item({index, question, selectedItem, clickFn}){
    const text = isSelected ? question : type;
    const isSelected = selectedItem === index;
    return (
        <ListItem button onClick={()=>{clickFn(isSelected ? -1 : index)}}>
            <ListItemIcon>
                <TranslateIcon />
            </ListItemIcon>
            <ListItemText primary={`${text}`} />
            {   
                isSelected ?
                    <VisibilityIcon/>
                :
                    <VisibilityOffIcon/>
            }
        </ListItem>
    )
}

// function Question({question, type, clickFn, isSelected, item}){
//     const [selectedQuestion, setSelectedQuestion] = useState({})
//     const text = isSelected ? question : type;
//     return (
//         <ListItem button onClick={()=>{clickFn(isSelected ? {} : item)}}>
//             <ListItemIcon>
//                 <TranslateIcon />
//             </ListItemIcon>
//             <ListItemText primary={`${text}`} />
//             {   
//                 isSelected ?
//                     <VisibilityIcon/>
//                 :
//                     <VisibilityOffIcon/>
//             }
//         </ListItem>
//     )
// }

function Answer({answer, type, clickFn, isSelected, item}){
    const text = isSelected ? answer : type;
    return (
        <ListItem button onClick={()=>{clickFn(isSelected ? {} : item)}}>
            <ListItemIcon>
                <TranslateIcon />
            </ListItemIcon>
            <ListItemText primary={`${text}`} />
            {   
                isSelected ?
                    <VisibilityIcon/>
                :
                    <VisibilityOffIcon/>
            }
        </ListItem>
    )
}


export default QuestionsAndAnswers;
