import React, {useState, useEffect} from 'react';
import List from '@material-ui/core/List';
import * as cardActions from '../actions/cardActions';
import {withRouter} from 'react-router-dom';
import Button from '@material-ui/core/Button';
import Player from './Player';
import * as _ from 'lodash';
import DeleteIcon from '@material-ui/icons/Delete';
import { useDispatch, useSelector } from "react-redux";

function Card({card, isMobile}){
    const [isShowAnswer, setIsShowAnswer] = useState(false);
    const [isShowHint, setIsShowHint] = useState(false);

    useEffect(()=>{
        setIsShowAnswer(false);
        setIsShowHint(false);
    }, [card])

    return (
        <div>
            {
                card.audioUrl ?
                    <Player url={card.audioUrl} controls={isMobile}></Player>
                :
                    null
            }
            <div className="study-text">{card.hiragana}</div>

            {
                isShowHint ? 
                    <div className="hint-text">{card.romaji}</div>
                    :
                    <div>
                        <Button color="secondary" onClick={()=>setIsShowHint(true)}>Show Hint</Button>
                    </div>

            }
            {
                isShowAnswer ? 
                    <div className="answer-text">{card.english}</div>
                    :
                    <div>
                        <Button color="primary" onClick={()=>setIsShowAnswer(true)}>Show Answer</Button>
                    </div>
            }
            <div></div>
        </div>
    )
}

function FlashCards(props, {isMobile}){
    const data = useSelector(state => state.cards);
    const dispatch = useDispatch();
    const [currentCardIndex, setCurrentCardIndex] = useState(0);
    const remaining = _.filter(data, o => {
        return !o.isLesson;
    });
    const finished = _.filter(data, o => {
        return o.isLesson;
    });

    useEffect(()=>{
        let type = props.match.params.lesson === 'all' ? null : props.match.params.lesson;
        dispatch(cardActions.getCards(type));
    },[]);

    function resetLesson(){
        dispatch(cardActions.resetLesson());
        setCurrentCardIndex(0);
    }

    function setNext(isRepeat){
        if(currentCardIndex+1 < remaining.length){
            if(!isRepeat){
                currentCard.isLesson = true;
                    dispatch(cardActions.setCard(currentCard)).then(res=>{
                });
            }else{
                setCurrentCardIndex(currentCardIndex+1);
            }
        }else{
            setCurrentCardIndex(0);
        }
    }

    let currentCard = {...remaining[currentCardIndex]};

    return currentCard ? (
        <div style={{textAlign: 'center', width: '100%'}} className="card-container">
            {
                finished.length > 0 ?
                    <div>
                        <Button
                            variant="contained"
                            color="secondary"
                            startIcon={<DeleteIcon />}
                            onClick={resetLesson}
                        >
                            Reset Lesson
                        </Button>
                    </div>
                :
                    null
            }

            <div style={{display: 'flex', justifyContent: 'space-evenly', marginBottom: '50px'}}>
                <div>Current: {currentCardIndex+1}</div>
                <div>Remaining in lesson: {remaining.length}</div>
                <div>Finished (Marked as Good): {finished.length}</div>
            </div>
            <List>
                <Card card={currentCard} isMobile={isMobile}></Card>
            </List>
            <div style={{display: 'flex', width: '100%', justifyContent: 'center'}}>
                <div style={{marginRight: '5px'}}>
                    <Button variant="contained" color="primary" onClick={()=>setNext(false)}>
                        Good
                    </Button>
                </div>
                <div style={{marginLeft: '5px'}}>
                    <Button variant="contained" color="secondary" onClick={()=>setNext(true)}>
                        Revisit
                    </Button>
                </div>
            </div>
        </div>
    ) : null
}

export default withRouter(FlashCards);
