import React, {useState, useEffect} from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import HelpOutlineIcon from '@material-ui/icons/HelpOutline';
import QuestionAnswerIcon from '@material-ui/icons/QuestionAnswer';
import VisibilityIcon from '@material-ui/icons/Visibility';
import VisibilityOffIcon from '@material-ui/icons/VisibilityOff';
import TranslateIcon from '@material-ui/icons/Translate';
import Divider from '@material-ui/core/Divider';
import Player from './Player';
import * as _ from 'lodash';

const defaultKeyShow = 'katakana';

function QuestionsAndAnswers({data}){
    const filtered = _.filter({...data}, o=>{
        return o.katakana !== null;
    })
    const [selectedItem, setSelectedItem] = useState({index: -1, type: ''});

    function handleClick(val){
        setSelectedItem(val);
    }

    return (
        <List>
            <Items data={filtered} handleClick={handleClick} selectedItem={selectedItem}></Items>
        </List>
    )
}

function Items({data, handleClick, selectedItem}){
    return data.map((item, i) => {
        return (
            <div key={i} className="question-container" style={{display: 'flex', justifyContent: 'space-between'}}>
                    <Questions questions={item.question} index={i}></Questions>
                {
                    item.answer ? 
                        <Answers answers={item.answer} index={i}></Answers>
                    :
                        null
                }
                <Divider light />
            </div>
        )
    });
}

function Questions({questions, index}){
    return (
        <div className="question">
            <ListItem button className={'voice-button'}>
                <ListItemIcon>
                    <HelpOutlineIcon/>
                </ListItemIcon>
                <ListItemText primary={`${questions[defaultKeyShow]}`}/>
                {
                    questions.audio ?
                        <Player url={questions.audio}></Player>
                    :
                        null
                }
            </ListItem>
            <Translations values={questions} index={index}></Translations>
        </div>
    )
}

function Answers({answers, index}){
    return (
        <div className="answer">
            <ListItem button className={'voice-button'}>
                <ListItemIcon>
                    <QuestionAnswerIcon/>
                </ListItemIcon>
                <ListItemText primary={`${answers[defaultKeyShow]}`}/>
                {
                    answers.audio ?
                        <Player url={answers.audio}></Player>
                    :
                        null
                }
            </ListItem>
            <Translations values={answers} index={index}></Translations>
        </div>
    )
}

function Translations({values, index}){
    const [selectedItem, setSelectedItem] = useState({index: -1, type: ''});

    return _.map(values, (val, key) => {
        if(key !== defaultKeyShow && key !== 'audio'){
            let isSelected = selectedItem.index === index && selectedItem.type === key,
                selectModel = {index: index, type: key};

            return (
                <ListItem className={`${isSelected ? 'selected' : ''}`} button onClick={()=>{setSelectedItem(isSelected ? {} : selectModel)}}>
                    <ListItemIcon>
                        <TranslateIcon />
                    </ListItemIcon>
                    <ListItemText primary={`${isSelected ? val : key}`} />
                    {   
                        isSelected ?
                            <VisibilityIcon/>
                        :
                            <VisibilityOffIcon/>
                    }
                </ListItem>
            )
        }
    })
}

function Answer({answer, type, clickFn, isSelected, item}){
    const text = isSelected ? answer : type;
    return (
        <ListItem button onClick={()=>{clickFn(isSelected ? {} : item)}}>
            <ListItemIcon>
                <TranslateIcon />
            </ListItemIcon>
            <ListItemText primary={`${text}`} />
            {   
                isSelected ?
                    <VisibilityIcon/>
                :
                    <VisibilityOffIcon/>
            }
        </ListItem>
    )
}


export default QuestionsAndAnswers;
