import React, {useState, useEffect} from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import LockOpenIcon from '@material-ui/icons/LockOpen';
import LockIcon from '@material-ui/icons/Lock';
import ListItemText from '@material-ui/core/ListItemText';
import Divider from '@material-ui/core/Divider';

function Questions({data}){
    const [selectedItem, setSelectedItem] = useState({index: -1, type: ''});

    function handleClick(index, type){
        setSelectedItem({index, type});
    }

    return (
        <List>
            <Items data={data} handleClick={handleClick} selectedItem={selectedItem}></Items>
        </List>
    )
}

function Items({data, handleClick, selectedItem}){
    return data.map((item, i) => {
        let isSelected = selectedItem.index === i && selectedItem.type === 'romaji';
        return (
            <div key={i}>
                {
                    item.question.map((question, key) => {
                        console.log(key)
                        return (
                            <Question question={item.question} type={'english'} clickFn={handleClick}></Question>
                        )
                    })
                }



                <Question question={item.question} type={'romaji'} clickFn={handleClick}></Question>
                <Question question={item.question} type={'katakana'} clickFn={handleClick}></Question>
                <Question question={item.question} type={'hiragana'} clickFn={handleClick}></Question>

                <Answer answer={item.question} type={'english'} clickFn={handleClick}></Answer>
                <Answer answer={item.question} type={'romaji'} clickFn={handleClick}></Answer>
                <Answer answer={item.question} type={'katakana'} clickFn={handleClick}></Answer>
                <Answer answer={item.question} type={'hiragana'} clickFn={handleClick}></Answer>
                <Divider light />
            </div>
        )
    });
}

function Question({question, type, clickFn, isSelected}){
    const text = question[type];
    return (
        <ListItem button onClick={clickFn}>
            <ListItemIcon>
            <LockOpenIcon />
            </ListItemIcon>
            <ListItemText primary={`${text}`} />
        </ListItem>
    )
}

function Answer({answer, type}){
    const text = answer[type];
    return (
        <ListItem button>
            <ListItemIcon>
            <LockOpenIcon />
            </ListItemIcon>
            <ListItemText primary={`${text}`} />
        </ListItem>
    )
}


export default Questions;
