import React, {useState, useEffect} from 'react';
import './App.css';
import { useDispatch, useSelector } from "react-redux";
import * as cardActions from './actions/cardActions';
import ExpansionPanel from '@material-ui/core/ExpansionPanel';
import ExpansionPanelSummary from '@material-ui/core/ExpansionPanelSummary';
import ExpansionPanelDetails from '@material-ui/core/ExpansionPanelDetails';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import QuestionsAndAnswers from './components/QuestionsAndAnswers';
import ListItemText from '@material-ui/core/ListItemText';
import ListItem from '@material-ui/core/ListItem';
import Phrases from './components/Phrases';
import * as _ from 'lodash';

function App() {
  const cards = useSelector(state => state.cards);
  const dispatch = useDispatch();
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    setIsMobile(window.outerWidth <= 767);

    const handleResize = () => {
      setIsMobile(window.outerWidth <= 767);
    };

    window.addEventListener("resize", handleResize, { passive: true });

    return () => {
      window.removeEventListener("resize", handleResize, true);
    }
  },[]);

  useEffect(()=>{
    dispatch(cardActions.getCards())
  },[]);

  const data = cards || null;
  const questions = _.filter(data, o=>{
    return o.type === "question";
  });
  const answers = _.filter(data, o=>{
    return o.type === "answer";
  });
  const phrases = _.filter(data, o=>{
    return o.type === "phrase";
  });

  return data ? (
    <div className="App" style={{width: '85%', margin: '0 auto'}}>      
      <ExpansionPanel>
        <ExpansionPanelSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="questions"
          id="questions"
        >
          Phrases
        </ExpansionPanelSummary>
        <ExpansionPanelDetails>
          <Phrases data={phrases} isMobile={isMobile}></Phrases>
        </ExpansionPanelDetails>
      </ExpansionPanel>

      <ExpansionPanel>
        <ExpansionPanelSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="cards"
          id="cards"
        >
          Flash Cards
        </ExpansionPanelSummary>
        <ExpansionPanelDetails>
         <FlashCards data={data}></FlashCards>
        </ExpansionPanelDetails>
      </ExpansionPanel>
    </div>
  ) : null;
}

export default App;
